                        }
                        
                        Spacer()
                        
                        VStack(alignment: .leading) {
                            Text("Last Status")
                                .font(.caption)
                                .foregroundColor(.gray)
                            Text("\(imageService.lastResponseStatus ?? 0)")
                                .font(.title2)
                                .fontWeight(.bold)
                                .foregroundColor(statusColor(imageService.lastResponseStatus))
                        }
                    }
                    .padding()
                    .background(Color.gray.opacity(0.1))
                    .cornerRadius(8)
                }
                
                // Last Request Details
                if let lastURL = imageService.lastRequestURL {
                    VStack(alignment: .leading, spacing: 8) {
                        Text("Last Request")
                            .font(.headline)
                            .fontWeight(.semibold)
                        
                        ScrollView {
                            Text(lastURL)
                                .font(.caption)
                                .foregroundColor(Color.slingBlue)
                                .multilineTextAlignment(.leading)
                        }
                        .frame(maxHeight: 60)
                        .padding()
                        .background(Color.slingLightBlue)
                        .cornerRadius(8)
                    }
                }
                
                // Error Details
                if let lastError = imageService.lastError {
                    VStack(alignment: .leading, spacing: 8) {
                        Text("Last Error")
                            .font(.headline)
                            .fontWeight(.semibold)
                            .foregroundColor(.red)
                        
                        ScrollView {
                            Text(lastError)
                                .font(.caption)
                                .foregroundColor(.red)
                                .multilineTextAlignment(.leading)
                        }
                        .frame(maxHeight: 80)
                        .padding()
                        .background(Color.red.opacity(0.1))
                        .cornerRadius(8)
                    }
                } else {
                    VStack(alignment: .leading, spacing: 8) {
                        Text("Status")
                            .font(.headline)
                            .fontWeight(.semibold)
                            .foregroundColor(.green)
                        
                        Text("No errors detected")
                            .font(.subheadline)
                            .foregroundColor(.green)
                            .padding()
                            .background(Color.green.opacity(0.1))
                            .cornerRadius(8)
                    }
                }
                
                // Troubleshooting Tips
                VStack(alignment: .leading, spacing: 8) {
                    Text("Troubleshooting")
                        .font(.headline)
                        .fontWeight(.semibold)
                    
                    VStack(alignment: .leading, spacing: 4) {
                        Text("‚Ä¢ Check internet connection")
                        Text("‚Ä¢ Verify Unsplash API key is valid")
                        Text("‚Ä¢ Look for HTTP status codes (403 = rate limit)")
                        Text("‚Ä¢ Tap orange error icons to retry")
                    }
                    .font(.caption)
                    .foregroundColor(.gray)
                    .padding()
                    .background(Color.yellow.opacity(0.1))
                    .cornerRadius(8)
                }
                
                Spacer()
            }
            .padding()
            .navigationTitle("Image Debug")
            .navigationBarTitleDisplayMode(.inline)
            .navigationBarItems(trailing: Button("Done") {
                isPresented = false
            })
        }
    }
    
    private func statusColor(_ status: Int?) -> Color {
        guard let status = status else { return .gray }
        
        switch status {
        case 200...299:
            return .green
        case 400...499:
            return .orange
        case 500...599:
            return .red
        default:
            return .gray
        }
    }
}



