                BettingInterfaceView(
                    bet: selectedBet,
                    selectedOption: selectedBetOption.isEmpty ? (selectedBet.options.first ?? "Yes") : selectedBetOption,
                    firestoreService: firestoreService,
                    onBetPlaced: nil
                )
            }
        }
    }
    

}

