// MARK: - Fullscreen Image View
struct FullscreenImageView: View {
    let imageUrl: String
    let selectedImage: UIImage?
    @Binding var isPresented: Bool
    @State private var scale: CGFloat = 1.0
    @State private var lastScale: CGFloat = 1.0
    @State private var offset: CGSize = .zero
    @State private var lastOffset: CGSize = .zero
    @State private var dismissOffset: CGSize = .zero
    @State private var isDismissing: Bool = false
    
    init(imageUrl: String, selectedImage: UIImage? = nil, isPresented: Binding<Bool>) {
        self.imageUrl = imageUrl
        self.selectedImage = selectedImage
        self._isPresented = isPresented
    }
    
    var body: some View {
        ZStack {
            Color.black.ignoresSafeArea()
                .opacity(isDismissing ? 1.0 - abs(dismissOffset.height) / 500.0 : 1.0)
            
            if let selectedImage = selectedImage {
                // Show selected image directly
                Image(uiImage: selectedImage)
                    .resizable()
                    .aspectRatio(contentMode: .fit)
                    .scaleEffect(scale)
                    .offset(x: offset.width + dismissOffset.width, y: offset.height + dismissOffset.height)
                    .opacity(isDismissing ? 1.0 - abs(dismissOffset.height) / 500.0 : 1.0)
                    .gesture(
                        SimultaneousGesture(
                            MagnificationGesture()
                                .onChanged { value in
                                    let delta = value / lastScale
                                    lastScale = value
                                    scale = min(max(scale * delta, 0.5), 4.0)
                                }
                                .onEnded { _ in
                                    lastScale = 1.0
                                    if scale < 1.0 {
                                        withAnimation(.easeInOut(duration: 0.3)) {
                                            scale = 1.0
                                            offset = .zero
                                        }
                                    }
                                },
                            DragGesture()
                                .onChanged { value in
                                    let newOffset = CGSize(
                                        width: lastOffset.width + value.translation.width,
                                        height: lastOffset.height + value.translation.height
                                    )
                                    offset = newOffset
                                }
                                .onEnded { _ in
                                    lastOffset = offset
                                }
                        )
                    )
            } else if !imageUrl.isEmpty {
                // Show image from URL
                AsyncImage(url: URL(string: imageUrl)) { image in
                    image
                        .resizable()
                        .aspectRatio(contentMode: .fit)
                        .scaleEffect(scale)
                        .offset(x: offset.width + dismissOffset.width, y: offset.height + dismissOffset.height)
                        .opacity(isDismissing ? 1.0 - abs(dismissOffset.height) / 500.0 : 1.0)
                        .gesture(
                            SimultaneousGesture(
                                MagnificationGesture()
                                    .onChanged { value in
                                        let delta = value / lastScale
                                        lastScale = value
                                        scale = min(max(scale * delta, 0.5), 4.0)
                                    }
                                    .onEnded { _ in
                                        lastScale = 1.0
                                        if scale < 1.0 {
                                            withAnimation(.easeInOut(duration: 0.3)) {
                                                scale = 1.0
                                                offset = .zero
                                            }
                                        }
                                    },
                                DragGesture()
                                    .onChanged { value in
                                        let newOffset = CGSize(
                                            width: lastOffset.width + value.translation.width,
                                            height: lastOffset.height + value.translation.height
                                        )
                                        offset = newOffset
                                    }
                                    .onEnded { _ in
                                        lastOffset = offset
                                    }
                            )
                        )
                } placeholder: {
                    ProgressView()
                        .progressViewStyle(CircularProgressViewStyle(tint: .white))
                        .scaleEffect(1.5)
                }
            } else {
                // Fallback
                ProgressView()
                    .progressViewStyle(CircularProgressViewStyle(tint: .white))
                    .scaleEffect(1.5)
            }
            
            VStack {
                HStack {
                    Spacer()
                    Button(action: {
                        isPresented = false
                    }) {
                        Image(systemName: "xmark.circle.fill")
                            .font(.title2)
                            .foregroundColor(.white)
                            .background(Color.black.opacity(0.5))
                            .clipShape(Circle())
                    }
                    .padding(.top, 50)
                    .padding(.trailing, 20)
                }
                Spacer()
            }
        }
        .onTapGesture(count: 2) {
            withAnimation(.easeInOut(duration: 0.3)) {
                if scale > 1.0 {
                    scale = 1.0
                    offset = .zero
                    lastOffset = .zero
                } else {
                    scale = 2.0
                }
            }
        }
        .gesture(
            DragGesture()
                .onChanged { value in
                    // Only allow dismiss gesture when not zoomed in
                    if scale <= 1.0 {
                        isDismissing = true
                        dismissOffset = value.translation
                    }
                }
                .onEnded { value in
                    if isDismissing {
                        // Check if swipe is significant enough to dismiss
                        let swipeThreshold: CGFloat = 100
                        let velocity = value.predictedEndTranslation.height - value.translation.height
                        
                        if abs(value.translation.height) > swipeThreshold || abs(velocity) > 500 {
                            // Dismiss the view
                            withAnimation(.easeOut(duration: 0.3)) {
                                isPresented = false
                            }
                        } else {
                            // Snap back to original position
                            withAnimation(.easeOut(duration: 0.3)) {
                                dismissOffset = .zero
                                isDismissing = false
                            }
                        }
                    }
                }
        )
    }
}

// MARK: - Message Bubble

struct MessageBubble: View {
    let message: CommunityMessage
    let isCurrentUser: Bool
    
    var body: some View {
        HStack {
            if isCurrentUser { Spacer() }
            
            VStack(alignment: isCurrentUser ? .trailing : .leading, spacing: 4) {
                if !isCurrentUser {
                    Text(message.senderName)
                        .font(.caption)
                        .fontWeight(.medium)
                        .foregroundColor(.gray)
                }
                
                Text(message.text)
                    .font(.subheadline)
                    .foregroundColor(isCurrentUser ? .white : .black)
                    .padding(.horizontal, 12)
                    .padding(.vertical, 8)
                    .background(isCurrentUser ? AnyShapeStyle(Color.slingGradient) : AnyShapeStyle(Color.white))
                    .cornerRadius(16)
                    .overlay(
                        RoundedRectangle(cornerRadius: 16)
                            .stroke(Color.gray.opacity(0.15), lineWidth: isCurrentUser ? 0 : 1)
                    )
                
                Text(formatMessageTime(message.timestamp))
                    .font(.caption2)
