                    }
                } else if userBets.isEmpty {
                    EmptyActiveBetsView(firestoreService: firestoreService)
                } else {
                    ForEach(userBets.prefix(5), id: \.id) { bet in
                        RecentBetRow(bet: bet)
                    }
                }
            }
            .padding(.horizontal, 16)
            .padding(.vertical, 20)
        }
        .refreshable {
            await refreshBets()
        }
