struct ChangePasswordView: View {
    @Environment(\.dismiss) private var dismiss
    @State private var currentPassword = ""
    @State private var newPassword = ""
    @State private var confirmPassword = ""
    @State private var isChanging = false
    @State private var showCurrentPassword = false
    @State private var showNewPassword = false
    @State private var showConfirmPassword = false
    @State private var showingSaveSuccess = false
    @State private var errorMessage = ""
    @State private var showingUnsavedChangesAlert = false
    
    var body: some View {
        NavigationView {
            ZStack {
                // Clean white background
                Color.white.ignoresSafeArea()
                
                VStack(spacing: 0) {
                    // Header
                    HStack {
                        Button(action: { 
                            if hasUnsavedChanges() {
                                showingUnsavedChangesAlert = true
                            } else {
                                dismiss()
                            }
                        }) {
                            Image(systemName: "arrow.left")
                                .font(.title2)
                                .foregroundColor(.black)
                                .frame(width: 44, height: 44)
                        }
                        
                        Spacer()
                        
                        Text("Change Password")
                            .font(.title2)
                            .fontWeight(.bold)
                            .foregroundColor(.black)
                        
                        Spacer()
                        
                        Button(action: {
                            changePassword()
                        }) {
                            Text("Save")
                                .font(.subheadline)
                                .fontWeight(.medium)
                                .foregroundColor(.slingBlue)
                        }
                        .disabled(isChanging)
                    }
                    .padding(.horizontal, 20)
                    .padding(.top, 20)
                    
                    // Content
                    VStack(spacing: 32) {
                        // Title
                        Text("Update Your Password")
                            .font(.largeTitle)
                            .fontWeight(.bold)
                            .foregroundColor(.black)
                            .multilineTextAlignment(.center)
                            .padding(.horizontal, 20)
                        
                        // Subtitle
                        Text("Choose a strong password to secure your account")
                            .font(.body)
                            .foregroundColor(.gray)
                            .multilineTextAlignment(.center)
                            .padding(.horizontal, 20)
                        
                        // Password input fields
                        VStack(alignment: .leading, spacing: 16) {
                            VStack(alignment: .leading, spacing: 8) {
                                Text("Current Password")
                                    .font(.headline)
                                    .foregroundColor(.black)
                                
                                if showCurrentPassword {
                                    TextField("Enter current password", text: $currentPassword)
                                        .textFieldStyle(ModernTextFieldStyle())
                                        .overlay(
                                            HStack {
                                                Spacer()
                                                
                                                Button(action: { showCurrentPassword.toggle() }) {
                                                    Image(systemName: "eye.slash")
                                                        .foregroundColor(.gray)
                                                        .frame(width: 24, height: 24)
                                                }
                                                .padding(.trailing, 20)
                                            }
                                        )
                                } else {
                                    SecureField("Enter current password", text: $currentPassword)
                                        .textFieldStyle(ModernTextFieldStyle())
                                        .overlay(
                                            HStack {
                                                Spacer()
                                                
                                                Button(action: { showCurrentPassword.toggle() }) {
                                                    Image(systemName: showCurrentPassword ? "eye.slash" : "eye")
                                                        .foregroundColor(.gray)
                                                        .frame(width: 24, height: 24)
                                                }
                                                .padding(.trailing, 20)
                                            }
                                        )
                                }
                            }
                            
                            VStack(alignment: .leading, spacing: 8) {
                                Text("New Password")
                                    .font(.headline)
                                    .foregroundColor(.black)
                                
                                if showNewPassword {
                                    TextField("Enter new password", text: $newPassword)
                                        .textFieldStyle(ModernTextFieldStyle())
                                        .overlay(
                                            HStack {
                                                Spacer()
                                                
                                                Button(action: { showNewPassword.toggle() }) {
                                                    Image(systemName: "eye.slash")
                                                        .foregroundColor(.gray)
                                                        .frame(width: 24, height: 24)
                                                }
                                                .padding(.trailing, 20)
                                            }
                                        )
                                } else {
                                    SecureField("Enter new password", text: $newPassword)
                                        .textFieldStyle(ModernTextFieldStyle())
                                        .overlay(
                                            HStack {
                                                Spacer()
                                                
                                                Button(action: { showNewPassword.toggle() }) {
                                                    Image(systemName: showNewPassword ? "eye.slash" : "eye")
                                                        .foregroundColor(.gray)
                                                        .frame(width: 24, height: 24)
                                                }
                                                .padding(.trailing, 20)
                                            }
                                        )
                                }
                            }
                            
                            VStack(alignment: .leading, spacing: 8) {
                                Text("Confirm New Password")
                                    .font(.headline)
                                    .foregroundColor(.black)
                                
                                if showConfirmPassword {
                                    TextField("Confirm new password", text: $confirmPassword)
                                        .textFieldStyle(ModernTextFieldStyle())
                                        .overlay(
                                            HStack {
                                                Spacer()
                                                
                                                Button(action: { showConfirmPassword.toggle() }) {
                                                    Image(systemName: "eye.slash")
                                                        .foregroundColor(.gray)
                                                        .frame(width: 24, height: 24)
                                                }
                                                .padding(.trailing, 20)
                                            }
                                        )
                                } else {
                                    SecureField("Confirm new password", text: $confirmPassword)
                                        .textFieldStyle(ModernTextFieldStyle())
                                        .overlay(
                                            HStack {
                                                Spacer()
                                                
                                                Button(action: { showConfirmPassword.toggle() }) {
                                                    Image(systemName: showConfirmPassword ? "eye.slash" : "eye")
                                                        .foregroundColor(.gray)
                                                        .frame(width: 24, height: 24)
                                                }
                                                .padding(.trailing, 20)
                                            }
                                        )
                                }
                            }
                            
                            Text("Must be at least 6 characters")
                                .font(.caption)
                                .foregroundColor(.gray)
                        }
                        .padding(.horizontal, 24)
                        
                        Spacer()
                    }
                    .padding(.top, 40)
                }
            }
            .navigationBarHidden(true)
            .alert("Password Changed", isPresented: $showingSaveSuccess) {
                Button("OK") { dismiss() }
            } message: {
                Text("Your password has been successfully updated.")
            }
            .alert("Error", isPresented: .constant(!errorMessage.isEmpty)) {
                Button("OK") { errorMessage = "" }
            } message: {
                Text(errorMessage)
            }
            .alert("Unsaved Changes", isPresented: $showingUnsavedChangesAlert) {
                Button("Leave", role: .destructive) { dismiss() }
                Button("Stay", role: .cancel) { }
            } message: {
                Text("You have unsaved changes. Are you sure you want to leave?")
            }
        }
    }
    
    private func hasUnsavedChanges() -> Bool {
        return !currentPassword.isEmpty || !newPassword.isEmpty || !confirmPassword.isEmpty
    }
    
    private func changePassword() {
        // Validate input
        guard !currentPassword.isEmpty && !newPassword.isEmpty && !confirmPassword.isEmpty else {
            errorMessage = "Please fill in all fields"
            return
        }
        
        guard newPassword == confirmPassword else {
            errorMessage = "New passwords do not match"
            return
        }
        
        guard newPassword.count >= 6 else {
            errorMessage = "Password must be at least 6 characters"
            return
        }
        
        isChanging = true
        
        // Simulate password change (you'll need to implement this in FirestoreService)
        DispatchQueue.main.asyncAfter(deadline: .now() + 1.5) {
            self.isChanging = false
            self.showingSaveSuccess = true
        }
    }
}

