                            .font(.title2)
                            .foregroundColor(.gray)
                            .frame(width: 44, height: 44)
                    }
                }
                .padding(.horizontal, 16)
                .padding(.top, 12)
                .padding(.bottom, 20)
                .background(Color.white)
                
                ScrollView {
                    VStack(spacing: 24) {
                        VStack(spacing: 16) {
                            HStack {
                                Text("Select Language")
                                    .font(.headline)
                                    .fontWeight(.semibold)
                                    .foregroundColor(.black)
                                Spacer()
                            }
                            
                            VStack(spacing: 0) {
                                ForEach(languages, id: \.self) { language in
                                    HStack {
                                        SettingsRow(icon: "globe", title: language, subtitle: "", isDestructive: false, showArrow: false, action: {})
                                        Spacer()
                                        if selectedLanguage == language {
                                            Image(systemName: "checkmark")
                                                .foregroundColor(.slingBlue)
                                                .font(.caption)
                                                .scaleEffect(0.8)
                                        }
                                    }
                                    if language != languages.last {
                                        Divider().padding(.leading, 56)
                                    }
                                }
                            }
                            .background(Color.white)
                            .cornerRadius(12)
                        }
                    }
                    .padding(.horizontal, 16)
                    .padding(.vertical, 20)
                }
            }
            .background(Color.gray.opacity(0.05))
            .navigationBarHidden(true)
            .onAppear {
                loadCurrentUserSettings()
            }
        }
    }
    
    private func saveLanguage() {
        print("üîß Saving language preference")
        
        // Save language preference to Firestore
        firestoreService.updateLanguageSetting(language: selectedLanguage) { success in
            DispatchQueue.main.async {
                if success {
                    print("‚úÖ Language preference saved to Firestore")
                    self.dismiss()
                } else {
                    print("‚ùå Failed to save language preference")
                    // You could show an error alert here
                }
            }
        }
    }
    
    private func loadCurrentUserSettings() {
        guard let user = firestoreService.currentUser else { return }
        
        // Load current language setting
        if let language = user.language {
            selectedLanguage = language
            print("üîß Loaded language setting from Firestore: \(language)")
        }
    }
}

