struct ContactSupportView: View {
    @Environment(\.dismiss) private var dismiss
    @ObservedObject var firestoreService: FirestoreService
    @State private var messageText = ""
    @State private var subject = ""
    @State private var isSubmitting = false
    @State private var showSuccessAlert = false
    @State private var showErrorAlert = false
    @State private var errorMessage = ""
    
    var body: some View {
        NavigationView {
            VStack(spacing: 0) {
                HStack {
                    Button(action: { dismiss() }) {
                        Image(systemName: "arrow.left")
                            .font(.title2)
                            .foregroundColor(.slingBlue)
                            .frame(width: 44, height: 44)
                    }
                    
                    Button(action: {
                        submitContactRequest()
                    }) {
                        Text("Submit")
                            .font(.subheadline)
                            .fontWeight(.medium)
                            .foregroundColor(.slingBlue)
                    }
                    .disabled(isSubmitting)
                    
                    Spacer()
                    
                    Text("Contact Us")
                        .font(.title2)
                        .fontWeight(.bold)
                        .foregroundColor(.black)
                    
                    Spacer()
                    
                    Button(action: { dismiss() }) {
                        Image(systemName: "xmark")
                            .font(.title2)
                            .foregroundColor(.gray)
                            .frame(width: 44, height: 44)
                    }
                }
                .padding(.horizontal, 16)
                .padding(.top, 12)
                .padding(.bottom, 20)
                .background(Color.white)
                
                ScrollView {
                    VStack(spacing: 24) {
                        VStack(spacing: 16) {
                            HStack {
                                Text("Send us a message")
                                    .font(.headline)
                                    .fontWeight(.semibold)
                                    .foregroundColor(.black)
                                Spacer()
                            }
                            
                            VStack(spacing: 16) {
                                VStack(alignment: .leading, spacing: 8) {
                                    Text("Subject")
                                        .font(.subheadline)
                                        .fontWeight(.medium)
                                        .foregroundColor(.black)
                                    
                                    TextField("Enter subject", text: $subject)
                                        .textFieldStyle(RoundedBorderTextFieldStyle())
                                        .padding(.horizontal, 16)
                                        .padding(.vertical, 12)
                                        .background(Color.white)
                                        .cornerRadius(8)
                                }
                                
                                VStack(alignment: .leading, spacing: 8) {
                                    Text("Message")
                                        .font(.subheadline)
                                        .fontWeight(.medium)
                                        .foregroundColor(.black)
                                    
                                    TextEditor(text: $messageText)
                                        .frame(minHeight: 120)
                                        .padding(.horizontal, 16)
                                        .padding(.vertical, 12)
                                        .background(Color.white)
                                        .cornerRadius(8)
                                        .overlay(
                                            RoundedRectangle(cornerRadius: 8)
                                                .stroke(Color.gray.opacity(0.3), lineWidth: 1)
                                        )
                                }
                                
                                Button(action: submitContactRequest) {
                                    HStack {
                                        if isSubmitting {
                                            ProgressView()
                                                .progressViewStyle(CircularProgressViewStyle(tint: .white))
                                                .scaleEffect(0.8)
                                        } else {
                                            Text("Submit Request")
                                                .font(.headline)
                                                .fontWeight(.semibold)
                                        }
                                    }
                                    .foregroundColor(.white)
                                    .frame(maxWidth: .infinity)
                                    .padding(.vertical, 16)
                                    .background(Color.slingBlue)
                                    .cornerRadius(12)
                                }
                                .disabled(messageText.isEmpty || subject.isEmpty || isSubmitting)
                                .opacity(messageText.isEmpty || subject.isEmpty ? 0.6 : 1.0)
                            }
                            .padding(.horizontal, 16)
                            .padding(.vertical, 20)
                            .background(Color.white)
                            .cornerRadius(12)
                        }
                    }
                    .padding(.horizontal, 16)
                    .padding(.vertical, 20)
                }
            }
            .background(Color.gray.opacity(0.05))
            .navigationBarHidden(true)
            .alert("Success", isPresented: $showSuccessAlert) {
                Button("OK") {
                    dismiss()
                }
            } message: {
                Text("Your message has been sent successfully. We'll get back to you soon!")
            }
            .alert("Error", isPresented: $showErrorAlert) {
                Button("OK") { }
            } message: {
                Text(errorMessage)
            }
        }
    }
    
    private func submitContactRequest() {
        guard !messageText.isEmpty && !subject.isEmpty else { return }
        
        isSubmitting = true
        
        // Create contact request data
        let _: [String: Any] = [
            "subject": subject,
            "message": messageText,
            "timestamp": Date(),
            "status": "pending",
            "deviceInfo": getDeviceInfo(),
            "consoleLogs": getConsoleLogs()
        ]
        
        // Here you would save to Firestore "Contact" collection
        // For now, we'll simulate the submission
        DispatchQueue.main.asyncAfter(deadline: .now() + 1.5) {
            isSubmitting = false
            showSuccessAlert = true
        }
    }
    
    private func getDeviceInfo() -> String {
        let device = UIDevice.current
        return "\(device.model) - \(device.systemName) \(device.systemVersion)"
    }
    
    private func getConsoleLogs() -> String {
        // In a real app, you might want to capture console logs
        // For now, return a placeholder
        return "Console logs captured at \(Date())"
    }
}

