struct RecentBetRow: View {
    let bet: FirestoreBet
    
    var body: some View {
        HStack(spacing: 12) {
            // Bet Image or Fallback Icon
            if let imageUrl = bet.image_url, !imageUrl.isEmpty {
                AsyncImage(url: URL(string: imageUrl)) { image in
                    image
                        .resizable()
                        .aspectRatio(contentMode: .fill)
                } placeholder: {
                    RoundedRectangle(cornerRadius: 8)
                        .fill(Color.gray.opacity(0.2))
                        .overlay(
                            ProgressView()
                                .scaleEffect(0.8)
                        )
                }
                .frame(width: 40, height: 40)
                .clipShape(RoundedRectangle(cornerRadius: 8))
            } else {
                // Fallback icon based on bet type
                RoundedRectangle(cornerRadius: 8)
                    .fill(Color.gray.opacity(0.2))
                    .frame(width: 40, height: 40)
                    .overlay(
                        Image(systemName: getBetTypeIcon(bet.bet_type))
                            .font(.caption)
                            .foregroundColor(.gray)
                    )
            }
            
            VStack(alignment: .leading, spacing: 2) {
                Text(bet.title)
                    .font(.subheadline)
                    .fontWeight(.medium)
                    .foregroundColor(.black)
                    .lineLimit(1)
                
                Text("Deadline: \(formatDeadline(bet.deadline))")
                    .font(.caption)
                    .foregroundColor(.gray)
            }
            
            Spacer()
            
            Text(bet.status.uppercased())
                .font(.caption)
                .fontWeight(.medium)
                .foregroundColor(.white)
                .padding(.horizontal, 8)
                .padding(.vertical, 4)
                .background(bet.status == "open" ? Color.green : Color.gray)
                .cornerRadius(8)
        }
        .padding(12)
        .background(Color.white)
        .cornerRadius(12)
    }
    
    private func formatDate(_ date: Date) -> String {
        let formatter = DateFormatter()
        formatter.dateFormat = "MMM d"
        return formatter.string(from: date)
    }
    
    // Helper function to format deadline
    private func formatDeadline(_ date: Date) -> String {
        let formatter = DateFormatter()
        formatter.dateFormat = "MMM d, yyyy"
        return formatter.string(from: date)
    }
    
    private func getBetTypeIcon(_ betType: String) -> String {
        switch betType.lowercased() {
        case "sports":
            return "sportscourt.fill"
        case "politics":
            return "building.columns.fill"
        case "entertainment":
            return "tv.fill"
        case "weather":
            return "cloud.sun.fill"
        case "finance":
            return "chart.line.uptrend.xyaxis"
        case "technology":
            return "laptopcomputer"
        case "health":
            return "heart.fill"
        case "education":
            return "book.fill"
        default:
            return "questionmark.circle.fill"
        }
    }
}

